INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'Role-playing (RPG), Shooter', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Uma saga de ficção científica onde suas escolhas definem o destino da galáxia.', 'A trilogia Mass Effect acompanha a jornada do Comandante Shepard para salvar a galáxia de uma raça de seres mecânicos conhecidos como Reapers. Combinando elementos de RPG com um sistema de tiro em terceira pessoa, a série é aclamada por sua narrativa complexa e o impacto duradouro das decisões do jogador.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Um épico sobre honra e lealdade no fim da era do Velho Oeste americano.', 'No papel de Arthur Morgan, um fora da lei da gangue Van der Linde, os jogadores exploram um vasto e detalhado mundo aberto. O jogo é uma obra-prima narrativa que explora a lealdade, a decadência de um estilo de vida e a inevitável chegada da modernidade, tudo isso com uma jogabilidade imersiva e visualmente deslumbrante.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'Um caçador de monstros profissional busca por sua filha adotiva em um mundo de fantasia sombria.', 'Como Geralt de Rívia, o jogador embarca em uma jornada pessoal e épica para encontrar Ciri, uma jovem com poderes extraordinários perseguida pela Caçada Selvagem. O jogo é famoso por seu gigantesco mundo aberto, missões secundárias com narrativas ricas e decisões morais complexas que moldam o mundo e seus habitantes.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'Um shinobi busca vingança em uma reimaginação sombria do Japão do período Sengoku.', 'Desenvolvido pela FromSoftware, Sekiro se destaca por seu sistema de combate preciso e implacável, focado em aparar golpes e quebrar a postura dos inimigos. O jogador controla o "Lobo", um guerreiro desonrado que deve resgatar seu mestre, enfrentando inimigos desafiadores e explorando um mundo interconectado.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2012, 'Role-playing (RPG), Adventure', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'Um samurai deve abandonar seu código de honra para salvar sua ilha da invasão mongol.', 'Jin Sakai se vê dividido entre o caminho do samurai e as táticas furtivas do "Fantasma" para libertar a ilha de Tsushima. O jogo é uma carta de amor ao cinema de samurai, com um mundo aberto estonteante, combate de espadas visceral e uma direção de arte que cria paisagens inesquecíveis.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'O clássico definitivo de plataforma que introduziu o dinossauro Yoshi ao mundo.', 'Lançado para o Super Nintendo, Super Mario World expandiu a fórmula de sucesso da série com um mapa interconectado, múltiplos segredos e a adição de Yoshi, que trouxe novas mecânicas de jogo. É celebrado por seu level design genial, controles precisos e seu charme atemporal que influenciou gerações.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Explore um vasto reino de insetos em um metroidvania aclamado pela crítica.', 'Com um estilo de arte 2D desenhado à mão, Hollow Knight convida os jogadores a desvendar os mistérios do reino em ruínas de Hallownest. O jogo combina exploração desafiadora, combate rápido e uma atmosfera melancólica, sendo considerado um dos melhores jogos independentes já feitos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platform', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Uma emocionante aventura de plataforma com uma narrativa tocante e visuais deslumbrantes.', 'O jogador controla Ori, um espírito guardião, em uma jornada para salvar a floresta de Nibel da escuridão. O jogo é conhecido por sua arte pintada à mão, trilha sonora orquestrada e uma história emocional sobre amor, perda e esperança. Sua jogabilidade de plataforma é fluida e desafiadora.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'Xbox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Ação frenética inspirada nos desenhos animados da década de 1930.', 'Famoso por seu estilo visual único, que recria perfeitamente a estética das animações clássicas, Cuphead é um jogo de plataforma e "run-and-gun" com foco em batalhas épicas contra chefes. Sua alta dificuldade, trilha sonora de jazz e animações fluidas o tornaram um sucesso instantâneo.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'A icônica aventura do ouriço azul com uma mecânica de viagem no tempo.', 'Considerado um dos melhores jogos da série, Sonic CD introduziu a mecânica de viajar entre o passado, presente e futuro das fases para garantir um "bom futuro". O jogo é lembrado por seu level design criativo, a introdução de Metal Sonic e suas memoráveis trilhas sonoras.');
